mapper.select:
select

from task_job_record


        and record_id = #{query.recordId,jdbcType=BIGINT}


        and job_id = #{query.jobId,jdbcType=BIGINT}


        and resource_id = #{query.resourceId,jdbcType=BIGINT}


        and job_status = #{query.jobStatus,jdbcType=INTEGER}


        and is_delete = #{query.isDelete,jdbcType=INTEGER}


        and created_time >= #{query.createdTimeBegin,jdbcType=TIMESTAMP}


        and created_time <= #{query.createdTimeEnd,jdbcType=TIMESTAMP}


        and updated_time >= #{query.updatedTimeBegin,jdbcType=TIMESTAMP}


        and updated_time <= #{query.updatedTimeEnd,jdbcType=TIMESTAMP}
